sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Create an aggregate object  
    var aggNeedIt = new GlideAggregate('x_58872_needit_needit');

    // Add query parameters  
    aggNeedIt.addQuery('active', '=', true);
    aggNeedIt.addQuery('u_requested_for', '=', current.u_requested_for);

    // Add aggregate  
    aggNeedIt.addAggregate('COUNT');

    // Execute query 
    aggNeedIt.query();

    // Process returned records
    while (aggNeedIt.next()) {
        // Create a message to display the count of the open NeedIt requests for the user in the Requested for field

        var msg = current.u_requested_for.name + " has " + aggNeedIt.getAggregate('COUNT', 'u_requested_for') + " open requests.";
        gs.addInfoMessage(msg);

    }

})(current, previous);
